  max-width: 370px;
  margin: 0 auto;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  margin-bottom: 10px;
}
.fave_images_container:first-child {
  margin-top: 10px;
}
.fave_images_container img {
  height: 200px;
  width: 95% !important;
}
.fav_recipe_details {
  position: absolute;
  background: red;
  top: 0;
  right: 0;
  height: 100%;
  grid-area: right;
  width: 100%;
  transform: translateX(500px);
  transition: transform 0.2s ease-in-out;
}
.close {
  transform: translateX(0);
}
.button_tab {
  display: none;
}

@media only screen and (max-width: 678px) {
  main {
    grid-template-columns: 1fr;
    grid-template-rows: 100%;
    grid-template-areas:
      "top top top top top"
      "left"
      "middle"
      "right";
  }
  .left_container {
    grid-area: top;
    position: relative;
    min-height: 100% !important;
    background-color: yellow;
    overflow-y: auto;
  }
  .recipe__container {
    display: flex;
    justify-content: center;
    align-items: center;
    flex-wrap: wrap;
    margin-top: 25px !important;
    /* overflow: scroll; */
    width: 100%;
    /* min-height: calc(100% - 50px); */
  }
  .recipe__container > li {
    width: 95%;
    max-width: 245px !important;
    /* height: 250px !important; */
    /* margin-right: 8px; */
    background-color: red;
    height: 200px;
    /* margin: 0 !important; */
  }
  .loader_container {
    margin-top: 50px;
  }

  .recipe_details {
    grid-area: top;
  }
  .fav_recipe {
    grid-area: top;
  }
  .fave_images_container {
    height: 100%;
    width: 100%;
  }
  .fave_images_container li {
    height: 200px;
    width: 90% !important;
  }
  .fave_images_container:first-child {
    margin-top: 75px;
  }

  .navigation {
    position: absolute;
    top: 0;
    left: 0;
    grid-area: left;
    z-index: 10;
    width: 100%;
  }
  nav {
    background: linear-gradient(to right, #642b73, #c6426e);
    justify-content: space-around !important;
    align-items: center !important;
    cursor: default;
  }
  .search_icon {
    display: block !important;
    width: 60px;
  }
  .search_icon img {
    width: 40px;
    cursor: pointer;
  }
  .logo {
    width: 150px;
    z-index: 100;
  }
  .logo h1 {
    color: #f1f1f1;
    font-size: 20px;
    /* font-weight: bold; */
  }

  .search {
    position: absolute;
    top: 0;
    left: 0;
    height: 100%;
    width: 100%;
    display: flex;
    justify-content: center;
    align-items: center;
    background: linear-gradient(to left, #642b73, #c6426e);
    z-index: 100;
    visibility: hidden;
    transition: opacity 0.2s ease-out;
  }
  .active {
    visibility: visible;
  }

  nav form {
    width: 90% !important;
    max-width: 350px;
  }
  form #search {
    width: 90%;
    height: 40px;
    padding: 0 0 !important;
  }
  form #search:active,
  form #search:focus {
    border: 1px solid #642b73;
  }
  .search_button {
    width: 100px;
    font-size: 18px;
  }
  .fave_container {
    display: none;
  }

  .button_tab {
    position: fixed;
    left: 0;
    bottom: 0;
    display: block !important;
    background: linear-gradient(to bottom, #642b73, #c6426e);
    width: 100%;
    height: 50px;
    z-index: 10;
    box-shadow: 2px 2px 2px #642b73;
    /* display: none !important; */
  }
  .button {
    height: 100%;
    width: 100%;
    display: flex;
    justify-content: center;
    align-items: center;
  }
  .recipe_details_container {
    columns: 200px 2;
  }
  .mobile_nav {
    width: 100px;
  }
  .button > div {
    display: flex;
    height: 100%;
    width: 100%;
    flex-direction: column;
    align-items: center;
    justify-content: center;
  }
  .mobile_nav img,
  .fa-heart,
  .fa-filter,
  #theme img {
    height: 28px;
    cursor: pointer;
    font-size: 28px;
  }
  .mobile_nav span > small,
  #theme small,
  .filter small {
    font-size: 15px;
    font-family: "Open Sans", sans-serif;
    font-weight: 600;
    color: #f1f1f1;
    text-align: center;
  }
  .small {
    display: none;
    transition: display 1s ease-in;
  }
  .hide_small {
    display: block;
  }

  .animate_page {
    transform: translateX(-800px);
    transition: transform 0.1s ease-in;
  }
  .hide_page {
    transform: translateX(0);
  }

  .left {
    display: none;
  }
}
